IF OBJECT_ID('FN_VERIFICA_FIRMA') IS NOT NULL
	DROP FUNCTION FN_VERIFICA_FIRMA
GO
CREATE FUNCTION FN_VERIFICA_FIRMA( @dni CHAR(8), @contraseña varchar(100))
RETURNS varchar
AS
	BEGIN
		DECLARE @resultado varchar

		IF EXISTS(SELECT * FROM PERSONA WHERE nDNI=@dni AND contraseña=@contraseña )
			BEGIN
				SELECT @resultado=firma FROM PERSONA WHERE nDNI=@dni AND contraseña=@contraseña 
			END
		ELSE
			BEGIN
			set @resultado='NO'
			END

		RETURN @resultado
	END
GO


IF OBJECT_ID('SP_PRUEBA') IS NOT NULL
	DROP PROC SP_PRUEBA
GO
CREATE PROC SP_PRUEBA( @dni CHAR(8), @contraseña varchar(100))
AS
	IF EXISTS(SELECT * FROM PERSONA WHERE nDNI=@dni AND contraseña=@contraseña)
		SELECT firma FROM PERSONA WHERE nDNI=@dni AND contraseña=@contraseña
	ELSE
		SELECT fechaNac FROM PERSONA
GO

